import React, { useState } from 'react';

function Form(){
    const [formVal,setFormVal] = useState({
        username:'',
        email:'',
        occupation:'',
        
        languages:[]
    });
    const onchangeHandler = (e)=>{
        console.log(e.target.value);

    }
    return(
        <>
        <h1 style={{textAlign:'center'}}>All Inputs fields htmlForm</h1>
        <div className='myform'>
            <form>
                <label htmlFor='username'>Username:</label>
                <input type='text' placeholder='username' name='username' onChange={onchangeHandler}/>
                <br/><br/>
                <label htmlFor='email'>Email:</label>
                <input type='text' placeholder='email'/>
                <br/><br/>
                <label htmlFor='occupation'>Occupation:</label>
                <select>
                    <option value='enginner'>Engineer</option>
                    <option value='doctor'>Doctor</option>
                    <option value='actor'>Actor</option>
                </select>
                <br/><br/>
                <label htmlFor='gender'>Gender:</label>&nbsp;
                Male:<input type='radio' name='gender' value='male'/>&nbsp;
                Female:<input type='radio' name='gender' value='female'/>&nbsp;
                Others:<input type='radio' name='gender' value='other'/>
                < br/><br/>
                <label htmlFor='languages'>Languages:</label>&nbsp;
                English:<input type='checkbox' name='languages' value='english'/>&nbsp;
                Spanish:<input type='checkbox' name='languages' value='spanish'/>&nbsp;
                Japanase:<input type='checkbox' name='languages' value='japanase'/>
                < br/><br/>
                <input type='submit' className='btn btn-success' value='Submit'/>

                
            </form>
        </div>
        </>
    )
}
export default Form;
