        {listData !== [] && listData.map((data,i)=>{
           return(
            <>
            <p key={i} style={{border:'1px solid green'}}>{data}</p>
            <button onClick={()=>removeActivity(i)}>Remove(-)</button>
          </>
           )
        })}
        {listData.length >=1 && <button onClick={()=>removeAll()}>Remove All</button>}
        
        </div>
        </div>
        </>
    )
}
